      default: false,
    },
    backgroundColor: {
      type: String,
    },
    color: {
      type: String,
    },
  },

  setup(props) {
    props = reactive(props);
    return {
      classes: computed(() => ({
        'storybook-button': props.primary || props.iconLeft || props.iconRight,
        'storybook-button--medium': props.primary || props.iconLeft || props.iconRight,
        'storybook-button--primary': props.primary || props.iconLeft || props.iconRight,
        'storybook-button--icon': props.icon,
      })),
      style: computed(() => ({
        backgroundColor: props.backgroundColor,
        color: props.color,
      })),
    }
  },
};
</script>
